	/***********************************************************************************************************
		Default Requirements
	***********************************************************************************************************/
	if(file_exists("../settings.php")) { require_once("../settings.php"); }
		else { @http_response_code(503); echo "Critical Error [c0]: <br />This CMS is not yet installed. <br />Please install this CMS by visiting the websites root folder!"; exit(); }
		
	/***********************************************************************************************************
		Requirements Initialize.php
	***********************************************************************************************************/
	if(file_exists($object['path']."/_core/initialize.php")) { require_once($object['path']."/_core/initialize.php"); }
		else { @http_response_code(503); echo "Critical Error [c1]: <br />Misconfigured \$object['path'] in settings.php. <br />Please check your configuration file."; exit(); }


	/***********************************************************************************************************
		Default Favicon
	***********************************************************************************************************/
	$favi_code = '<link rel="icon" href="data:image/x-icon;base64,">';

	/***********************************************************************************************************
		Defer Site if Required
	***********************************************************************************************************/
	if(defined("_HIVE_DEFERSITE_LOGOUT_")) {
		if(_HIVE_DEFERSITE_LOGOUT_) {
			Header("Location: "._HIVE_DEFERSITE_LOGOUT_."");
			exit();
		}
	}
	
	/***********************************************************************************************************
		Redirect if Site not Enabled
	***********************************************************************************************************/
	if(defined("_HIVE_ENABLESITE_LOGOUT_")) {
		if(!_HIVE_ENABLESITE_LOGOUT_) {
			$object["csrf"] = new x_class_csrf(_HIVE_COOKIE_."__login_csrf_logout");
			hive__default_volt_header($object, $object["lang"]->translate("string_error"), $favi_code, "dark");
			?>
			<section class="vh-lg-100 mt-5 mt-lg-0 bg-soft d-flex align-items-center">
				<div class="container">
					<div class="row justify-content-center form-bg-image" >
						<div class="col-12 d-flex align-items-center justify-content-center">
							<div class="bg-white shadow border-0 rounded border-light p-4 p-lg-5 w-100 fmxw-500">
								This page is currently deactivated by _HIVE_ENABLESITE_LOGOUT_!
								<br /><br /><a href="../" class="btn btn-gray-200 mt-2"><?php echo hive__hsc($object["lang"]->translate("hive_login_mc_backtohome")); ?></a>
							</div>
						</div>
					</div>
				</div>
			</section>					
			<?php 
			hive__default_volt_footer();
			exit();
		}
	}	

	/***********************************************************************************************************
		Overrite with Site Module / Extension File if Present
	***********************************************************************************************************/
	if(!_HIVE_INTERNAL_VERSION_ERROR_) {
		$object["hive_mode_config"] = hive__init_site_header($object, _HIVE_MODE_);
		// Extension Libraries
		foreach ($object["extensions_path"] as $hive_extension_loader_current_init) {
			if (file_exists($hive_extension_loader_current_init."/_user/user_logout.php")) {
				$object["extension"] = hive__init_extension_header($object, basename($hive_extension_loader_current_init)); 
				require_once $hive_extension_loader_current_init."/_user/user_logout.php";
				exit();
			}
		} unset($object["extension"]);
		// Sitemod
		if(file_exists($object["path"]."/_site/"._HIVE_MODE_."/_user/user_logout.php")) { 
			require_once($object["path"]."/_site/"._HIVE_MODE_."/_user/user_logout.php"); 
			exit();
		} 
	} unset($object["hive_mode_config"]); unset($object["extension"]);		

	/***********************************************************************************************************
		Logout Functionalities
	***********************************************************************************************************/
	session_destroy();
	if (isset($_SERVER['HTTP_COOKIE'])) {
		$cookies = explode(';', $_SERVER['HTTP_COOKIE']);
		$path = parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH);
		foreach($cookies as $cookie) {
			$parts = explode('=', $cookie);
			$name = trim($parts[0]);
			setcookie($name, '', time() - 3600, $path);
			setcookie($name, '', time() - 3600, '/');
		}
	}
	Header("Location: ../");
	exit();